// Lambda style
NSNotificationCenter.DefaultCenter.AddObserver (
        NSApplication.DidBecomeActiveNotification, (notification) => {Console.WriteLine ("Received the notification NSApplication", notification); }


// Method style
void Callback (NSNotification notification)
{
    Console.WriteLine ("Received a notification NSApplication", notification);
}

void Setup ()
{
    NSNotificationCenter.DefaultCenter.AddObserver (NSApplication.DidBecomeActiveNotification, Callback);
}

